import { Component, OnInit } from '@angular/core';
import { FlightFilter } from '../flight-filter';
import { FlightService } from '../flight.service';
import { Flight } from '../flight';
import { Employee } from '../employee';


@Component({
  selector: 'app-flight',
  templateUrl: 'flight-list.component.html'
})
export class FlightListComponent implements OnInit {

  filter = new FlightFilter();
  selectedFlight!: Flight;
  feedback: any = {};

  get flightList(): Employee[] {
    return this.flightService.flightList;
  }

  constructor(private flightService: FlightService) {
  }

  ngOnInit() {
    this.search();
  }

  search(): void {
    console.log("serachinnnnnnnnnnnnnnnnnnng")
    this.flightService.load(this.filter);
  }

  select(selected: Flight): void {
    this.selectedFlight = selected;
  }

  delete(flight: Employee): void {
    if (confirm('Are you sure?')) {
      this.flightService.delete(flight).subscribe(() => {
          this.feedback = {type: 'success', message: 'Delete was successful!'};
          setTimeout(() => {
            this.search();
          }, 1000);
        },
        err => {
          this.feedback = {type: 'warning', message: 'Error deleting.'};
        }
      );
    }
  }
}
<nav aria-label="breadcrumb">
  <ol class="breadcrumb">
    <li class="breadcrumb-item"><a routerLink="/">Department</a></li>
    <li class="breadcrumb-item active">Employee</li>
  </ol>
</nav>

<div class="card">
  <div class="header">
    <h2 class="title">Employee List</h2>
  </div>

  <div class="content">
    <form #f="ngForm">

      <!-- <div class="form-group">
        <label for="from">From:</label>
        <input [(ngModel)]="filter.from" id="from" name="from" class="form-control">
      </div>

      <div class="form-group">
        <label for="to">To:</label>
        <input [(ngModel)]="filter.to" id="to" name="to" class="form-control">
      </div> -->

      <!-- <button (click)="search()" [disabled]="!f?.valid" class="btn btn-primary">Search</button> -->
      <a [routerLink]="['../flights', 'new' ]" class="btn btn-default ml-2">New</a>
    </form>
  </div>
</div>

<div class="card" *ngIf="flightList.length > 0">
  <div *ngIf="feedback.length" class="alert alert-{{feedback.type}}">{{ feedback.message }}</div>
  <div class="table-responsive">
    <table class="table table-centered table-hover mt-2" id="datatable">
      <thead>
        <tr>
          <th class="border-top-0" scope="col">Id</th>
          <th class="border-top-0" scope="col">FirstName</th>
          <th class="border-top-0" scope="col">LastName</th>
          <th class="border-top-0" scope="col">Date</th>
          <th class="border-top-0" scope="col" style="width:120px"></th>
        </tr>
      </thead>
      <tbody>
        <tr *ngFor="let item of flightList">
        <!-- </tr> [class.active]="item === selectedFlight"> -->

          <td>{{item._id}}</td>
          <td>{{item.firstName}}</td>
          <td>{{item.lastName}}</td>
          <td>{{item.date | date}}</td>
          <td style="white-space: nowrap">
            <a [routerLink]="['../employees', item._id ]" class="btn btn-secondary">Edit</a>&nbsp;
            <button (click)="delete(item)" class="btn btn-danger">Delete</button>
          </td>
        </tr>
      </tbody>
    </table>
  </div>
</div>
