import { Component, OnInit } from '@angular/core';
import { ActivatedRoute } from '@angular/router';
import { Router } from '@angular/router';
import { FlightService } from '../flight.service';
import { Flight } from '../flight';
import { map, switchMap } from 'rxjs/operators';
import { of } from 'rxjs';
import { Employee } from '../employee';

@Component({
  selector: 'app-flight-edit',
  templateUrl: './flight-edit.component.html'
})
export class FlightEditComponent implements OnInit {

  id!: string;
  flight!: Employee;
  feedback: any = {};

  constructor(
    private route: ActivatedRoute,
    private router: Router,
    private flightService: FlightService) {
  }

  ngOnInit() {
    this
      .route
      .params
      .pipe(
        map(p => p.id),
        switchMap(id => {
          if (id === 'new') { return of(new Employee()); }
          return this.flightService.findById(id);
        })
      )
      .subscribe(flight => {
          this.flight = flight;
          this.feedback = {};
        },
        err => {
          this.feedback = {type: 'warning', message: 'Error loading'};
        }
      );
  }

  save() {
    // this.flightService.save(this.flight).subscribe(
    //   flight => {
    //     this.flight = flight;
    //     this.feedback = {type: 'success', message: 'Save was successful!'};
    //     setTimeout(() => {
    //       this.router.navigate(['/flights']);
    //     }, 1000);
    //   },
    //   err => {
    //     this.feedback = {type: 'warning', message: 'Error saving'};
    //   }
    // );
  }

  cancel() {
    this.router.navigate(['/flights']);
  }
}
<nav aria-label="breadcrumb">
  <ol class="breadcrumb">
    <li class="breadcrumb-item"><a routerLink="/">Home</a></li>
    <li class="breadcrumb-item active">Flight</li>
  </ol>
</nav>
<div class="card">
  <div class="header">
    <h2 class="title">Flight Detail</h2>
  </div>
  <div class="content">
    <div *ngIf="feedback.length" class="alert alert-{{feedback.type}}">{{ feedback.message }}</div>
    <form *ngIf="flight" #editForm="ngForm" (ngSubmit)="save()">

      <div class="form-group">
        <label for="firstName">FirstName</label>
        <input [(ngModel)]="flight.firstName" id="firstName" name="firstName" class="form-control">
      </div>

      <div class="form-group">
        <label for="lastName">LastName</label>
        <input [(ngModel)]="flight.lastName" id="lastName" name="lastName" class="form-control">
      </div>


      <div class="form-group">
        <label for="date">Date</label>
        <input type="date" [(ngModel)]="flight.date" id="date" name="date" class="form-control">
      </div>

      <button type="submit" class="btn btn-primary" [disabled]="!editForm.form.valid">Save</button>
      <button type="button" class="btn btn-secondary" (click)="cancel()">Cancel</button>
    </form>
  </div>
</div>
